// https://v3.nuxtjs.org/api/configuration/nuxt.config
export default defineNuxtConfig({
  extends: [
    '@nuxt-themes/elements',
    '@nuxt-themes/typography',
  ],
  modules: [
    '@nuxtjs/tailwindcss',
    '@nuxt/content',
    '@nuxtjs/color-mode',
    '@vueuse/nuxt',
    'nuxt-icon'
  ],
  css: [
    '@/assets/styles/globals.postcss'
  ],
  app: {
    baseURL: '/blog/'
  },
  colorMode: {
    preference: 'dark',
    fallback: 'dark',
    classSuffix: ''
  },
  content: {
    documentDriven: true,
    highlight: {
      theme: {
        dark: 'github-dark',
        default: 'github-light'
      }
    }
  }
})

import colors from 'tailwindcss/colors'

/** @type {import('tailwindcss').Config} */
export default {
  darkMode: 'class',
  theme: {
    extend: {
      colors: {
        primary: colors.gray
      }
    }
  }
}

import { defineTheme } from 'pinceau'
import colors from 'tailwindcss/colors'

// See Typography config on https://github.com/nuxt-themes/typography/blob/main/tokens.config.ts
export default defineTheme({
  color: {
    primary: colors.teal
  },
  typography: {
    color: {
      secondary: colors.gray
    }
  },
  prose: {
    code: {
      block: {
        backgroundColor: {
          initial: colors.gray['50'],
          dark: colors.black
        }
      },
      inline: {
        backgroundColor: {
          initial: colors.gray['100'],
          dark: colors.black
        }
      }
    },
    hr: {
      color: {
        initial: colors.gray['300'],
        dark: colors.gray['50']
      }
    }
  }
})

export default defineAppConfig({
  prose: {
    h1: { icon: 'material-symbols:link-rounded' },
    h2: { icon: 'material-symbols:link-rounded' },
    h3: { icon: 'material-symbols:link-rounded' },
    h4: { icon: 'material-symbols:link-rounded' },
    h5: { icon: 'material-symbols:link-rounded' },
  }
})
